import React, { Component } from 'react';
import axios from 'axios';


export default class ListTout extends Component {

    constructor(props) {
        super(props);
        this.state = { produit: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/api/users/newArticle')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ produit: response.data });
                console.log('i am a produit', this.state.produit)
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <div>
            <div className="container-fluid">

                {
                    (this.state.produit.length > 0) ? (this.state.produit.map((obj) => {
                        return <div className="container-fluid">
                            <div id="ligne" className="row" key={obj._id}>
                                <div className="container">
                                    <img className="img-fluid " width="50%" height="20%"
                                    src={'http://localhost:8080/api/users/newArticleImage/' + obj.image} 
                                    alt="pdp" />
                                </div>
                                <div className="container">
                                    <div className="container-fluid">
                                        <div className="row">
                                            <div className="col-md-3"></div>
                                        </div>
                                    </div>
                                </div>
                                {/* <div className="col-md-2">
                                    <div className="row">
                                        <div className="container-fluid">
                                            <h2>
                                                {obj.titre}
                                            </h2>
                                            <p><strong>{obj.prix}</strong></p>
                                        </div>

                                    </div>
                                </div> */}
                                <div className="container">
                                <button class="btn btn-success" onClick={() => {
                                            confirmAlert({
                                                customUI: ({ onClose }) => {
                                                    return (
                                                        <center>
                                                            <div className='custom-ui'>
                                                                <div >
                                                                    <h4>Inscription pour l 'Atelier {obj.Titre}</h4>
                                                                    <input name="Nom" onChange={this.handleChange}
                                                                        value={this.state.value} placeholder="Entrer votre  nom" /><br></br>
                                                                    <input name="Prenom" placeholder="Entrer votre  prenom" onChange={this.handleChange} value={this.state.value} /><br></br>
                                                                    <input name="Telephone" placeholder="Entrer votre  Telephone" onChange={this.handleChange} value={this.state.value} /><br></br>
                                                                    <input name="Email" placeholder="Entrer votre numero email" onChange={this.handleChange} value={this.state.value} /><br></br>

                                                                    <button
                                                                        onClick={() => {
                                                                            axios.post("http://localhost:8080/particulier/" + obj._id, {
                                                                                Nom: this.state.Nom,
                                                                                Prenom: this.state.Prenom,
                                                                                Telephone: this.state.Telephone,
                                                                                Email: this.state.Email

                                                                            }).then(res => {
                                                                                console.log(res.data);
                                                                                axios.get("http://localhost:8080/atelier").then(res => {

                                                                                    this.setState({ profil: res.data })
                                                                                    console.log(this.state.profil)

                                                                                })
                                                                            })

                                                                            onClose();
                                                                        }}
                                                                    >
                                                                        confirmer
                          </button>
                                                                    <button onClick={onClose}>No</button>
                                                                </div>

                                                            </div></center>
                                                    );
                                                }
                                            });
                                        }} >S'inscrire</button>
                                </div>
                            </div>
                        </div>

                    })) : ('')
                }

            </div>
        </div>
    }
    render() {
        return (
            <div>
                {this.liste()}
            </div>
        );
    }
}
