import React, { Component } from 'react';
import axios from 'axios';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';

export default class ListTout extends Component {

    constructor(props) {
        super(props);
        this.state = { 
            nom: '',
        prenom: '',
        email: '',
        phone: '',
profil: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/api/users/newArticle')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ produit: response.data });
                console.log('i am a produit', this.state.produit)
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <div>
            <div className="container-fluid">
            <div className="row view-group" id="colonne">

                {
                    (this.state.profil.length > 0) ? (this.state.profil.filter((params)=>params.visib).map((chaqVal) => {
                        return <div class="item col-xs-3 col-lg-3" id="carte">
                            <div className="card card-cascade narrower card-ecommerce">
                                    <img width="auto" id="imageproduit" height="230px"
                                        src={'http://localhost:8080/api/users/newArticleImage/' + chaqVal.image}
                                        alt="pdp" />
                                <div className="card-body card-body-cascade">
                                    <center>
                                        <h6 id="description">
                                            <span id="nomproduit">
                                            {chaqVal.titre}
                                            </span>
                                            </h6>
                                            </center>
                                            <div className="row">
                                                    <div className="col-md-6">

                                                        <p className="card-text">
                                                            <strong>
                                                                <span id="description">Description</span></strong>&nbsp;&nbsp;
                                                                 <div id="point">
                                                                 {chaqVal.description}
                                                                 </div>
                                                                  </p>
                                                        <p className="card-text">
                                                            <strong>
                                                                <span id="description">
                                                                    Date</span></strong>&nbsp;&nbsp;
                                                                     <div id="point">
                                                                     {chaqVal.date}
                                                                     </div> 
                                                                     </p>
                                                        <p className="card-text">
                                                            <strong>
                                                                <span id="description">
                                                                    Nombre de place disponible
                                                                    </span>
                                                                    </strong>&nbsp;&nbsp; 
                                                                    <div id="point">{chaqVal.place}
                                                                    </div>
                                                                </p>
                                                    </div>
                                                    <div className="col-md-6">
                                                        <p className="card-text">
                                                            <strong><span id="description">
                                                                Heure du debut
                                                                </span></strong>&nbsp;&nbsp; 
                                                                <div id="point">
                                                                {chaqVal.debut}
                                                                </div> </p>
                                                        <p className="card-text"  id="colonne2">
                                                            <strong><span id="description">
                                                                Durée de l'atelier</span>
                                                                </strong>&nbsp;&nbsp; <div id="point">
                                                                {chaqVal.duree}
                                                                </div> </p>
                                                        <p className="card-text">
                                                            <strong><span id="description">
                                                                Nombre de place reserve
                                                                </span></strong>&nbsp;&nbsp; <div id="point">
                                                                {chaqVal.placeRes}
                                                                </div> </p>
                                                    </div>
</div>
                                </div>
                                <span className="spanprix">
                                            <strong>Prix: {chaqVal.prix} Ar</strong>
</span><br />
                                {/* <div className="col-md-2">
                                    <div className="row">
                                        <div className="container-fluid">
                                            <h2>
                                                {obj.titre}
                                            </h2>
                                            <p><strong>{obj.prix}</strong></p>
                                        </div>

                                    </div>
                                </div> */}
                                <div className="container">
                                    <button class="btn btn-success" onClick={() => {
                                        confirmAlert({
                                            customUI: ({ onClose }) => {
                                                return (
                                                    <center>
                                                        <div className='custom-ui'>
                                                            <div >
                                                                <h2 id="h2popups">S'inscrire de l'atelier : {chaqVal.titre}</h2>
                                                                <input name="nom" onChange={this.handleChange} value={this.state.value} placeholder="Nom"  required/><br></br>
                                                                <input name="prenom" placeholder="Prenom" onChange={this.handleChange} value={this.state.value} required/><br></br>
                                                                <input name="phone" placeholder="Numero téléphone" onChange={this.handleChange} value={this.state.value} /><br></br>
                                                                <input name="email" placeholder="Email" onChange={this.handleChange} value={this.state.value} required/><br></br>

                                                                <button onClick={() => {
                                                                    axios.post("http://localhost:8080/api/users/particulier/" + chaqVal._id, {
                                                                        nom: this.state.nom,
                                                                        prenom: this.state.prenom,
                                                                        phone: this.state.phone,
                                                                        email: this.state.email

                                                                    }).then(res => {
                                                                        console.log(res.data);
                                                                        axios.get("http://localhost:8080/api/users/newArticle").then(res => {

                                                                            this.setState({ profil: res.data })
                                                                            console.log(this.state.profil)

                                                                        })
                                                                    })

                                                                    onClose();
                                                                }}
                                                                >
                                                                    confirmer
                                                                </button>
                                                                <button onClick={onClose}>No</button>
                                                            </div>

                                                        </div></center>
                                                );
                                            }
                                        });
                                    }} >S'inscrire</button>
                                </div>
                            </div>
                        </div>

                    })) : ('')
                }
                </div>

            </div>
        </div>
    }
    render() {
        return (
            <div>
                {this.liste()}
            </div>
        );
    }
}
